import createFontTag from '../fontsLoader';

const googleType = 'google-font';
test('FL.1 Test a valid google-font', () => {
  const src =
    'https://fonts.googleapis.com/css?family=Tangerine:bold,bolditalic|Inconsolata:italic|Droid+Sans';
  const theFont = {
    type: googleType,
    src: src,
  };
  const tag = createFontTag(document, theFont);
  expect(tag.getAttribute('href')).toBe(src);
});

test('FL.2 Test a non - valid google-font', () => {
  const src =
    'https://another.domain/css?family=Tangerine:bold,bolditalic|Inconsolata:italic|Droid+Sans';
  const theFont = {
    type: googleType,
    src: src,
  };
  const tag = createFontTag(document, theFont);
  expect(tag.getAttribute('href')).toBe(null);
});

test('FL.3 Test a non - valid font type', () => {
  const src =
    'https://another.domain/css?family=Tangerine:bold,bolditalic|Inconsolata:italic|Droid+Sans';
  const theFont = {
    type: 'non-valid',
    src: src,
  };
  const tag = createFontTag(document, theFont);
  expect(tag.getAttribute('href')).toBe(null);
});
