import { parse } from '../../../../_coreUtils/templateEngine/parser';
import { isFunction, isString } from '../../../../_coreUtils/helper';

export default function compileHTML(template, initParams, isHTML = false) {
  if (isFunction(template)) {
    return template(initParams);
  }
  const res = parse(template, { items: { initParams }, keys: {} }, isHTML);
  if (isString(res)) {
    return res;
  }
  return res.outerHTML;
}
