import NonBlockingErrorClip from '../NonBlockingErrorClip';

test(`NonBlockingErrorClip proxy test`, () => {
  const MyObj = {
    addIncident() {
      return false;
    },

    x: 1,
    y: 2,
  };

  const proxy = NonBlockingErrorClip(MyObj, ['error 1', 'error 2']);
  expect(proxy.x).toBe(1);
  expect(proxy.y).toBe(2);
  expect(proxy.nonBlockingErrorClip).toBe(true);
  expect(proxy.errors).toEqual(['error 1', 'error 2']);
  expect(proxy.addIncident()).toEqual({ result: true });
});
