export function prepareElementsAttrs(staggerAttrs, index) {
  const toReturn = [];
  for (let i = 0; i < staggerAttrs.length; i++) {
    toReturn.push({
      path: staggerAttrs[i].path,
      value: staggerAttrs[i].values[index],
    });
  }
  return toReturn;
}

export function prepareElementsProps(staggerProps, index) {
  const toReturn = [];
  for (let i = 0; i < staggerProps.length; i++) {
    toReturn.push({
      path: staggerProps[i].path,
      value: staggerProps[i].values[index],
    });
  }
  return toReturn;
}
