function getIncidentsByChannel(adjustMillisecond = 0, parentMillisecond = 0) {
  const incidentsByPlugin = {};
  incidentsByPlugin[this.mc_plugin_npm_name] = [
    {
      millisecond: adjustMillisecond,
      parentMillisecond,
      incident: this,
      id: this.id,
    },
  ];
  return incidentsByPlugin;
}

export default getIncidentsByChannel;
