import SchemaAdaptor from './SchemaAdaptor';
import { validator } from './core';

// export default validator;
export default {
  compile: function (schema) {
    const adaptor = new SchemaAdaptor(schema, validator);
    const adapted = adaptor.adaptSchema();
    return validator.compile(adapted);
  },
  validate: function (value, schema) {
    const adaptor = new SchemaAdaptor(schema, validator);
    const adapted = adaptor.adaptSchema();
    return validator.validate(value, adapted);
  },
};
