const registeredDVs = [];
import attribute from './attribute/index';
import expression from './expression/index';
import initParams from './initParams/index';
import pattern from './pattern/index';
import stagger from './stagger/index';

export function registerDV(definition) {
  registeredDVs.push(definition);
}

export function getDVs() {
  return registeredDVs;
}

registerDV(attribute);
registerDV(expression);
registerDV(initParams);
registerDV(pattern);
registerDV(stagger);
