import conf from './conf';
import { validateExpression } from './_helpers';

function validate(expression, logError) {
  const res = validateExpression(expression);
  if (!res.result) {
    logError(`Invalid ${conf.preface} expression`);
  }
  return res.result;
}
export default validate;
